function displayOrder(){const products=JSON.parse(localStorage.getItem("products"));document.querySelector(".orderSummary").innerHTML=`\n    <h2 class="h4 text-center">Merci pour votre commande ! Voici le récapitulatif : </h2>\n        <br/>\n        <br/>\n    <h3 class="text-center h5"><strong>Numéro de commande :</strong> ${localStorage.getItem("orderId")}</h3>\n        <br/>\n        <br/>\n    <div class="row content">\n    </div>     \n    <p class="text-center mt-1">Montant total de votre commande : <strong> ${localStorage.getItem("total")},00 &euro; </strong></p>`;const content=document.querySelector(".content");for(let i=0;i<products.length;i++)content.innerHTML+=`\n<div class="card col col-md-4">\n    <img\n        class="card-img-top"\n        src="${products[i].image}"\n        alt="Oh le joli nounours !"\n    />\n    <div class="card-body teddyInfos text-dark">\n        <h5 class="card-title name">${products[i].name}</h5>\n        <p class="color">${products[i].option}</p>\n        <p class="price text-decoration-none">${products[i].price}.00 &euro;</p>\n        <p class="quantity">Quantité: ${products[i].quantity}</p>\n    </div>\n</div>`}