async function getTeddies(){return fetch("http://localhost:3000/api/teddies").then(response=>response.json()).then(teddies=>displayTeddiesCards(teddies)).catch(error=>{console.log("Erreur de connexion au serveur",error)})}function displayTeddiesCards(teddies){const element=document.querySelector("#productsList");for(let i=0;i<teddies.length;i++)element.innerHTML+=`\n                <div class="col-12 col-md-6 productCard list-group-item">\n                    <a\n                        href="product.html?${teddies[i]._id}"\n                        class="text-decoration-none productLink"\n                    >\n                        <img class="card-img-top img-fluid" src="${teddies[i].imageUrl}"  alt="Un joli nounours !" />\n                        <div class="card-body teddyInfos text-dark">\n                            <h5 class="card-title name">${teddies[i].name}</h5>\n                            <p class="price">${teddies[i].price/100}.00 &euro;</p>\n                            <p class="description"> ${teddies[i].description}</p>\n                        </div>\n                    </a>\n                </div>\n            `}